/* styles.css */

/* General Styles */
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    line-height: 1.6;
}

.container {
    width: 80%;
    margin: auto;
    overflow: hidden;
}

header {
    background: #35424a;
    color: #ffffff;
    padding-top: 30px;
    min-height: 70px;
    border-bottom: #e8491d 3px solid;
}

header a {
    color: #ffffff;
    text-decoration: none;
    text-transform: uppercase;
    font-size: 16px;
}

header ul {
    padding: 0;
    list-style: none;
}

header li {
    float: left;
    display: inline;
    padding: 0 20px 0 20px;
}

header #branding {
    float: left;
}

header nav {
    float: right;
    margin-top: 10px;
}

/* Navigation Bar */
nav ul {
    padding: 0;
    list-style: none;
}

nav li {
    display: inline;
}

nav a {
    color: #ffffff;
    text-decoration: none;
    padding: 15px 20px;
    display: inline-block;
}

nav a:hover,
nav a.current {
    background-color: #e8491d;
    color: #ffffff;
}

/* Main Content */
section {
    margin-bottom: 20px;
}

/* Menu Styles */
.menu-item {
    border-bottom: #ccc 1px solid;
    padding: 10px 0;
}

.menu-item:last-child {
    border: 0;
}

.menu-item h3 {
    margin-top: 0;
    color: #35424a;
}

/* Reservation Form Styles */
form {
    background-color: #f4f4f4;
    padding: 20px;
    border-radius: 10px;
}

form label {
    display: block;
    margin-bottom: 8px;
    color: #35424a;
}

form input[type="text"],
form input[type="date"],
form input[type="time"],
form input[type="number"] {
    width: calc(100% - 22px);
    padding: 10px;
    margin-bottom: 10px;
    border: #ddd 1px solid;
}

form button {
    background-color: #e8491d;
    color: #ffffff;
    padding: 10px 20px;
    border: none;
    cursor: pointer;
}

form button:hover {
    background-color: #cc3c1f;
}

/* Reservation Overview Styles */
.reservation-list {
    margin-top: 20px;
}

.reservation-item {
    background-color: #f9f9f9;
    padding: 15px;
    border-bottom: #ddd 1px solid;
}

.reservation-item:last-child {
    border-bottom: none;
}

.reservation-item h4 {
    margin-top: 0;
    color: #35424a;
}

/* Footer Styles */
footer {
    background: #35424a;
    color: #ffffff;
    text-align: center;
    padding: 10px 0;
    position: relative;
    clear: both;
}

// Import necessary modules
import { saveAs } from 'file-saver';

/**
 * Function to handle form submission and save reservation data.
 * @param {Event} event - The form submit event.
 */
function handleFormSubmit(event) {
  event.preventDefault();
  const formData = new FormData(event.target);
  const reservationData = Object.fromEntries(formData.entries());

  // Validate input
  if (!validateReservation(reservationData)) {
    alert('Please fill in all fields correctly.');
    return;
  }

  // Save reservation to JSON file
  saveReservationToJSON(reservationData);

  // Clear form after submission
  event.target.reset();
  alert('Reservation saved successfully!');
}

/**
 * Function to validate reservation data.
 * @param {Object} reservation - The reservation object containing name, date, time, and number of people.
 * @returns {boolean} - True if valid, false otherwise.
 */
function validateReservation(reservation) {
  return (
    reservation.name &&
    reservation.date &&
    reservation.time &&
    reservation.people > 0
  );
}

/**
 * Function to save reservation data to a JSON file.
 * @param {Object} reservation - The reservation object to be saved.
 */
async function saveReservationToJSON(reservation) {
  try {
    const response = await fetch('reservations.json', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });

    let reservations = [];
    if (response.ok) {
      reservations = await response.json();
    }

    reservations.push(reservation);

    const blob = new Blob([JSON.stringify(reservations, null, 2)], { type: 'application/json' });
    saveAs(blob, 'reservations.json');
  } catch (error) {
    console.error('Error saving reservation:', error);
    alert('Failed to save reservation. Please try again later.');
  }
}

// Attach event listener to the form
document.addEventListener('DOMContentLoaded', () => {
  const form = document.getElementById('reservationForm');
  if (form) {
    form.addEventListener('submit', handleFormSubmit);
  } else {
    console.error('Reservation form not found in the DOM.');
  }
});

