// script.js

/**
 * Handles form submission and saves reservation data to reservations.json.
 */

document.addEventListener('DOMContentLoaded', () => {
  const reservationForm = document.getElementById('reservation-form');
  if (reservationForm) {
    reservationForm.addEventListener('submit', handleFormSubmit);
  }
});

/**
 * Handles the form submission event.
 * @param {Event} event - The form submit event.
 */
async function handleFormSubmit(event) {
  event.preventDefault();

  const formData = new FormData(event.target);
  const reservationData = Object.fromEntries(formData.entries());

  try {
    await saveReservation(reservationData);
    alert('Reservierung erfolgreich!');
    event.target.reset();
  } catch (error) {
    console.error('Fehler beim Speichern der Reservierung:', error);
    alert('Es gab einen Fehler bei der Reservierung. Bitte versuchen Sie es erneut.');
  }
}

/**
 * Saves the reservation data to reservations.json.
 * @param {Object} reservationData - The reservation data to save.
 */
async function saveReservation(reservationData) {
  const response = await fetch('/reservations.json', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(reservationData)
  });

  if (!response.ok) {
    throw new Error('Network response was not ok');
  }
}

/**
 * Fetches and displays all reservations.
 */
async function fetchReservations() {
  const response = await fetch('/reservations.json');
  if (response.ok) {
    const reservations = await response.json();
    displayReservations(reservations);
  } else {
    console.error('Fehler beim Abrufen der Reservierungen:', response.statusText);
  }
}

/**
 * Displays the reservations on the page.
 * @param {Array} reservations - The array of reservation objects.
 */
function displayReservations(reservations) {
  const reservationsList = document.getElementById('reservations-list');
  if (reservationsList) {
    reservationsList.innerHTML = '';
    reservations.forEach(reservation => {
      const listItem = document.createElement('li');
      listItem.textContent = `${reservation.name} - ${reservation.datum} um ${reservation.uhrzeit} für ${reservation.personenzahl} Personen`;
      reservationsList.appendChild(listItem);
    });
  }
}

// Fetch and display reservations when the page loads
fetchReservations();

