// script.js

/**
 * Fetches reservations from reservations.json and displays them on the page.
 */
async function fetchAndDisplayReservations() {
  try {
    // Fetch the reservations data from reservations.json
    const response = await fetch('reservations.json');
    if (!response.ok) {
      throw new Error('Network response was not ok ' + response.statusText);
    }
    const reservations = await response.json();

    // Get the container where reservations will be displayed
    const reservationsContainer = document.getElementById('reservations-container');

    // Clear any existing content in the container
    reservationsContainer.innerHTML = '';

    // Iterate over each reservation and create a display element for it
    reservations.forEach(reservation => {
      const reservationElement = document.createElement('div');
      reservationElement.className = 'reservation';
      reservationElement.innerHTML = `
        <p><strong>Name:</strong> ${reservation.name}</p>
        <p><strong>Datum:</strong> ${new Date(reservation.date).toLocaleDateString()}</p>
        <p><strong>Uhrzeit:</strong> ${reservation.time}</p>
        <p><strong>Personenzahl:</strong> ${reservation.numberOfPeople}</p>
      `;
      reservationsContainer.appendChild(reservationElement);
    });
  } catch (error) {
    console.error('There was a problem with the fetch operation:', error);
    const reservationsContainer = document.getElementById('reservations-container');
    reservationsContainer.innerHTML = '<p>Fehler beim Laden der Reservierungen.</p>';
  }
}

// Call the function to display reservations when the page loads
document.addEventListener('DOMContentLoaded', fetchAndDisplayReservations);
