/* styles.css */

/* Reset some default browser styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    background-color: #f4f4f9;
    color: #333;
}

.container {
    width: 80%;
    margin: 0 auto;
    overflow: hidden;
}

header {
    background: #35424a;
    color: #fff;
    padding-top: 30px;
    min-height: 70px;
    border-bottom: #e8491d 3px solid;
}

header a {
    color: #fff;
    text-decoration: none;
    text-transform: uppercase;
    font-size: 16px;
}

header ul {
    padding: 0;
    list-style: none;
}

header li {
    float: left;
    display: inline;
    padding: 0 20px 0 20px;
}

header #branding {
    float: left;
}

header nav {
    float: right;
    margin-top: 10px;
}

header .highlight, header .current a {
    color: #e8491d;
    font-weight: bold;
}

header a:hover {
    color: #ccc;
    text-decoration: none;
}

#showcase {
    min-height: 400px;
    background: url('../img/coffee.jpg') no-repeat center center;
    background-size: cover;
    text-align: center;
    color: #fff;
    padding-top: 100px;
}

#showcase h1 {
    font-size: 55px;
    margin-bottom: 10px;
}

#showcase p {
    font-size: 20px;
}

.navbar-light .navbar-brand {
    color: #343a40;
}

/* Menu Styles */
.menu-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px 0;
    border-bottom: 1px solid #ddd;
}

.menu-item img {
    max-width: 100px;
    height: auto;
}

/* Reservation Form Styles */
.reservation-form {
    background: #fff;
    padding: 20px;
    margin-top: 20px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.reservation-form label {
    display: block;
    margin-bottom: 5px;
    font-weight: bold;
}

.reservation-form input[type="text"],
.reservation-form input[type="date"],
.reservation-form input[type="time"],
.reservation-form input[type="number"] {
    width: 100%;
    padding: 8px;
    margin-bottom: 20px;
    border: 1px solid #ddd;
    border-radius: 4px;
}

.reservation-form button {
    background: #e8491d;
    color: #fff;
    padding: 10px 20px;
    border: none;
    border-radius: 4px;
    cursor: pointer;
}

.reservation-form button:hover {
    background: #cc3a23;
}

/* Reservations Overview Styles */
.reservations-overview {
    margin-top: 20px;
}

.reservation-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px 0;
    border-bottom: 1px solid #ddd;
}

.reservation-item:last-child {
    border-bottom: none;
}

.reservation-item span {
    font-weight: bold;
}

// script.js

/**
 * Handles form submission and localStorage operations for a coffee shop reservation system.
 */

// Select DOM elements
const reservationForm = document.getElementById('reservation-form');
const reservationsList = document.getElementById('reservations-list');

// Load existing reservations from localStorage on page load
document.addEventListener('DOMContentLoaded', () => {
  renderReservations();
});

/**
 * Handles the submission of the reservation form.
 * @param {Event} event - The form submission event.
 */
function handleFormSubmit(event) {
  event.preventDefault();

  // Collect form data
  const formData = new FormData(reservationForm);
  const reservation = {
    name: formData.get('name'),
    date: formData.get('date'),
    time: formData.get('time'),
    persons: formData.get('persons')
  };

  // Save the reservation to localStorage
  saveReservation(reservation);

  // Clear form fields
  reservationForm.reset();

  // Render updated reservations list
  renderReservations();
}

/**
 * Saves a reservation to localStorage.
 * @param {Object} reservation - The reservation object to save.
 */
function saveReservation(reservation) {
  let reservations = JSON.parse(localStorage.getItem('reservations')) || [];
  reservations.push(reservation);
  localStorage.setItem('reservations', JSON.stringify(reservations));
}

/**
 * Renders the list of reservations from localStorage on the page.
 */
function renderReservations() {
  const reservations = JSON.parse(localStorage.getItem('reservations')) || [];

  // Clear existing reservations
  reservationsList.innerHTML = '';

  // Render each reservation
  reservations.forEach((reservation, index) => {
    const li = document.createElement('li');
    li.textContent = `Name: ${reservation.name}, Date: ${reservation.date}, Time: ${reservation.time}, Persons: ${reservation.persons}`;

    // Add delete button for each reservation
    const deleteButton = document.createElement('button');
    deleteButton.textContent = 'Delete';
    deleteButton.addEventListener('click', () => deleteReservation(index));
    li.appendChild(deleteButton);

    reservationsList.appendChild(li);
  });
}

/**
 * Deletes a reservation from localStorage.
 * @param {number} index - The index of the reservation to delete.
 */
function deleteReservation(index) {
  let reservations = JSON.parse(localStorage.getItem('reservations')) || [];
  reservations.splice(index, 1);
  localStorage.setItem('reservations', JSON.stringify(reservations));
  renderReservations();
}

// Attach form submission event listener
reservationForm.addEventListener('submit', handleFormSubmit);
