/* styles.css */

/* Reset some default browser styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    color: #333;
    background-color: #f4f4f9;
}

.container {
    width: 80%;
    margin: 0 auto;
    overflow: hidden;
}

/* Header styling */
header {
    background: #35424a;
    color: #fff;
    padding-top: 30px;
    min-height: 70px;
    border-bottom: #e8491d 3px solid;
}

header a {
    color: #fff;
    text-decoration: none;
    text-transform: uppercase;
    font-size: 16px;
}

header ul {
    padding: 0;
    list-style: none;
}

header li {
    float: left;
    display: inline;
    padding: 0 20px 0 20px;
}

header #branding {
    float: left;
}

header nav {
    float: right;
    margin-top: 10px;
}

/* Menu styling */
.menu {
    background: #fff;
    padding: 20px;
    margin-top: 20px;
    border-radius: 5px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.menu h2 {
    text-align: center;
    margin-bottom: 20px;
}

.menu ul {
    list-style: none;
    padding: 0;
}

.menu li {
    border-bottom: #ddd 1px solid;
    padding: 10px 0;
}

.menu a {
    color: #333;
    text-decoration: none;
}

/* Form styling */
form {
    background: #fff;
    padding: 20px;
    margin-top: 20px;
    border-radius: 5px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

form label {
    display: block;
    margin-bottom: 8px;
    color: #333;
}

form input[type="text"],
form input[type="email"],
form textarea {
    width: 100%;
    padding: 8px;
    margin-bottom: 20px;
    border: 1px solid #ddd;
    border-radius: 4px;
}

form button {
    background: #e8491d;
    color: #fff;
    padding: 10px 20px;
    border: none;
    border-radius: 4px;
    cursor: pointer;
}

form button:hover {
    background: #c6371f;
}

/* Responsive design */
@media (max-width: 768px) {
    header li {
        display: block;
        text-align: center;
    }

    .menu ul {
        padding: 0;
    }

    .menu li {
        border-bottom: none;
        margin-bottom: 10px;
    }
}

// script.js

/**
 * Handles localStorage operations and form submissions for the Coffee Shop website.
 */

// Select DOM elements
const reservationForm = document.getElementById('reservation-form');
const menuItems = document.querySelectorAll('.menu-item');

/**
 * Save a reservation to localStorage.
 * @param {Object} reservation - The reservation data to save.
 */
function saveReservation(reservation) {
  const reservations = JSON.parse(localStorage.getItem('reservations')) || [];
  reservations.push(reservation);
  localStorage.setItem('reservations', JSON.stringify(reservations));
}

/**
 * Handle form submission for reservations.
 * @param {Event} event - The form submission event.
 */
function handleFormSubmit(event) {
  event.preventDefault();
  const formData = new FormData(reservationForm);
  const reservation = {};
  formData.forEach((value, key) => {
    reservation[key] = value;
  });
  saveReservation(reservation);
  alert('Reservation saved successfully!');
  reservationForm.reset();
}

/**
 * Add click event listener to each menu item.
 */
function addMenuClickListeners() {
  menuItems.forEach(item => {
    item.addEventListener('click', () => {
      const itemName = item.querySelector('.item-name').textContent;
      alert(`You selected: ${itemName}`);
    });
  });
}

// Event listeners
reservationForm.addEventListener('submit', handleFormSubmit);

// Initialize page
addMenuClickListeners();

